mapper.select:
select * from ( select rownum as rn, f.* from ( select * from board_boardType_user_view where BOARD_TYPE_NAME like '공지사항' and BOARD_TYPE_COMMENT_OK like 'Y'
and USER_ID like '%' || #{keyword} || '%'
order by BOARD_REG_DATE desc
)f )A
where rn >= #{startRow} and rn <= #{endRow}

select *from
(
	select rownum as RNUM, A.* from
	(
		select * from board_boardType_user_view 
		where BOARD_DEL_FLAG is null and BOARD_TYPE_USE ='Y' 
		
		order by board_reg_date desc
	)A
)

where RNUM>#{firstRecordIndex} 
and RNUM<=#{firstRecordIndex} + #{recordCountPerPage}
